package Model;

public class Hodan {
	String makp;
	String mahd;
	int notv;
	int sonha;
	public Hodan() {
		// TODO Auto-generated constructor stub
	}
	public String getMakp() {
		return makp;
	}
	public void setMakp(String makp) {
		this.makp = makp;
	}
	public String getMahd() {
		return mahd;
	}
	public void setMahd(String mahd) {
		this.mahd = mahd;
	}
	public int getNotv() {
		return notv;
	}
	public void setNotv(int notv) {
		this.notv = notv;
	}
	public int getSonha() {
		return sonha;
	}
	public void setSonha(int sonha) {
		this.sonha = sonha;
	}
	

}
